svg.script:

let svgItems = document.getElementsByClassName('svg-item');
let shapes = document.getElementsByClassName('shape');

for (let i = 0; i < svgItems.length; i++) {
    let d = shapes[i].getAttribute('d');
    let cx = shapes[i].getAttribute('cx');
    let itemX, itemY;
    if (d != null) {
        let fromX = parseInt(d.split(' ')[1]);
        let fromY = parseInt(d.split(' ')[2]);
        let toX = parseInt(d.split(' ')[4]);
        let toY = parseInt(d.split(' ')[5]);
        itemX = parseInt((fromX + toX) / 2);
        itemY = parseInt((fromY + toY) / 2);
    }
    else if(cx != null) {
        let cx = shapes[i].getAttribute('cx');
        let cy = shapes[i].getAttribute('cy');
        itemX = parseInt(cx);
        itemY = parseInt(cy);
    }else {
        let cx = shapes[i].getAttribute('x');
        let cy = shapes[i].getAttribute('y');
        let cw = shapes[i].getAttribute('width') / 2;
        let ch = shapes[i].getAttribute('height') / 2;
        itemX = parseInt(cx) + parseInt(cw);
        itemY = parseInt(cy) + parseInt(ch);
    }
    svgItems[i].setAttribute('transform-origin', `${itemX} ${itemY}`);
}
